import ReactDOM from 'react-dom';
import "./index.css";
let time = new Date();


let currDate;
let hrs ;
let minutes ;
let sec ;

let timehere = document.getElementsByClassName('timing');
console.log(timehere);
setInterval
(
  ()=>
  {
    currDate = new Date();
    hrs =  currDate.getHours();
    minutes = (currDate.getMinutes()).toLocaleString();
    sec = currDate.getSeconds();
    timehere.innerHTML =`time is ${sec}`
  },
  1000);

// let currDate = new Date();
