import React from 'react';
import ReactDOM from 'react-dom';
import "./index.css";


let time = new Date();

// let currDate = new Date();
// let hrs =  currDate.getHours();
// let minutes = (currDate.getMinutes()).toLocaleString();
// let sec = currDate.getSeconds();




function currentTime() 
{
  let date = new Date(); 
  let hh = date.getHours();
  let mm = date.getMinutes();
  let ss = date.getSeconds();

   hh = (hh < 10) ? "0" + hh : hh;
   mm = (mm < 10) ? "0" + mm : mm;
   ss = (ss < 10) ? "0" + ss : ss;

   let time = hh + ":" + mm + ":" + ss + " ";

  document.getElementById("temp__time").innerText = time; 
  let t = setInterval(function(){ currentTime() }, 1000);
}

currentTime();





let samay  = time.getHours();
const cssStyle = {};
console.log("Anand"); 
let greeting = '';
if(samay>=5 && samay< 12){
  greeting = 'Good Morning';
  cssStyle.color = "Green";

  }
else if (samay>=12 && samay<16){
  greeting = 'Good Afternoon';
  cssStyle.color = "Orange";

}
else if (samay>=16 && samay<20){
  greeting = 'Good Evening';
  cssStyle.color = "Grey";
}
else if(samay>=20 && samay<=24){
  greeting = 'Good Night';
}
else{
  greeting = 'It\'s too late ....Go to Sleep !';
}
ReactDOM.render(
  <>
      <p className='heading'>Clock By Vedant</p>

  <div className = "container">
    <div className = "small_container">
      <h1>
      Hello there, I am Vedant.
    <br/>
         <span style={cssStyle}>{greeting}!</span></h1>
         <br />
         This is <span id='temp__time'> {ss}</span>

    </div>
  </div>
  </>,
  document.getElementById('root')
);
